      import { Link } from "react-router-dom";
      import { useState } from "react";

      function Register({ registerUser, buttonText }) {
        const [formValue, setFormValue] = useState({
          name: "",
          email: "",
          password: "",
        });

        function handleChange(e) {
          const { name, value } = e.target;

          setFormValue({
            ...formValue,
            [name]: value,
          });
        }

        function handleSubmit(e) {
          e.preventDefault();
          registerUser(formValue);
        }

    return(
        <div className="form">
            <form className='form__container' name='register' method="POST" onSubmit={handleSubmit} >
                <Link to='/' className='form__logo'>
                  <img className='form__logo-image' src={logo} alt='логотип' />
                </Link>
                <h2 className='form__title'>Добро пожаловать!</h2>
                <h3 className='form__input-title'>Имя</h3>
                <input className='form__input'   type="email"
          name="name"
          placeholder="name"
          autoComplete="userName"
          value={formValue.name}
          onChange={handleChange} minLength={2} maxLength={30} required></input>
                <span className='form__input-error'></span>
                <h3 className='form__input-title'>E-mail</h3>
                <input className='form__input'  placeholder='email' name='email'  type='email' required></input>
                <span className='form__input-error'>{errors.email}</span>
                <h3 className='form__input-title'>Пароль</h3>
                <input className='form__input input-password' onChange={handlePasswordChange} value={values.password}  type='password'  name='password' minLength={8} required></input>
                <span className='form__input-error'>{errors.password}</span>
                <button className='form__button' type='submit' >Зарегистрироваться</button>
                <div className='form__question'>
                    <p className='form__tag'>Уже зарегистрированы?</p>
                    <Link to="/sign-in" className='form__link'>Войти</Link>
                </div> 
            </form>
        </div>
    );
}

export default Register;
