import FilterCheckbox from "../FilterCheckBox/FilterCheckBox";
import { useEffect, useState } from "react";

function SearcForm({ handleShortFilms, shortMovies, onSubmit, defaultValue }) {
    const [errorText, setErrorText] = useState('');
    const [keyword, setKeyword]  = useState('');
    const [isFormValid, setIsFormValid] = useState(false);

    useEffect(() => {
        setKeyword(defaultValue)
    }, [defaultValue])

    const handleChange = (evt) => {
        setKeyword(evt.target.value);
        setIsFormValid(evt.target.closest('form').checkValidity());
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();
        if (keyword) {
            onSubmit(keyword);

        } else {
            return setErrorText('Нужно ввести ключевое слово');
        }
    };

    return(
        <section className="search">
            <form className="search__form" 
               id="form"
               noValidate
               onSubmit={handleSubmit}>
               <div className="search__field" >
                <input
                className="search__input"
                name="name"
                type="text"
                value={keyword}
                placeholder="Фильм"
                autoComplete="off"
                required
                ></input>
                <span className="search-error">{errorText.name}</span>
                <button className="search__button" type="submit" disabled></button>
                </div>
            </form>

            <FilterCheckbox shortMovies={shortMovies} handleShortFilms={handleShortFilms}/>
        </section>
    );
}

export default SearcForm
