                    (<button  className="profile__button-save" type="submit">Редактировать</button>)
                  }
                </form>
                <button type="submit" className="profile__logout" onClick={handleSignOut}>
                        Выйти из аккаунта
                    </button>
            </section>
        </>
    );
}

