import FilterCheckbox from "../FilterCheckBox/FilterCheckBox";
import { useEffect, useState } from "react";

function SearcForm({ handleShortFilms, shortMovies, handleSubmit, input  }) {
    const { values, errors, isValid, handleChange } =useFormWithValidation();  
    return(
        <section className="search">
            <form className="search__form" id="form" onSubmit={handleSubmit}>
               <div className="search__field" >
                <input
                className="search__input"
                name="name"
                type="text"
                onChange={handleChange} 
                value={values.name}
                placeholder="Фильм"
                autoComplete="off"
                defaultValue={input}
                required
                ></input>
                <span className="search-error">{errors.name}</span>
                <button className="search__button" type="submit" ></button>
                </div>
            </form>

            <FilterCheckbox shortMovies={shortMovies} handleShortFilms={handleShortFilms}/>
        </section>
    );
}

export default SearcForm
